import { Component, OnInit } from '@angular/core';
import { JwtService } from '../jwt.service';
import { FormGroup, FormBuilder, Validators ,NgForm} from '@angular/forms';

@Component({
  selector: 'app-displayleave',
  templateUrl: './displayleave.component.html',
  styleUrls: ['./displayleave.component.scss']
})
export class DisplayleaveComponent implements OnInit{
  registerForm!: FormGroup ;
  leavesedit!:any[];
  showModal: boolean = false;
 // updateForm!:FormGroup;


  leaves:any = null;
  constructor(private service:JwtService,private fb: FormBuilder){
    this.getLeaveDetails();
  }

  // ngOnInit(): void {
  //   this.registerForm=this.fb.group({
  //     name:['',[Validators.required]],
  //     maxLeaves:['',Validators.required]
  //   })
  // }
  ngOnInit(): void {

    this.registerForm=this.fb.group({
      name:['',[Validators.required]],
      maxLeaves:['',Validators.required]
    })
    this.service.getLeaveTypes().subscribe(
      (response: any[]) => {
        this.leavesedit = response;
      }
    );
  }
  leaveToUpdate={
    //console.log(this.leaveToUpdate);
    id:null,
    name:"",
    maxLeaves:null
  }


  getLeaveDetails(){
    this.service.getLeaves().subscribe(
      (resp)=>{
        console.log(resp);
        this.leaves=resp;
        console.log("this is",this.leaves.id);
      },
      (err)=>{
        console.log(err);
      }
    )
  }

  delete(leave:any){
    this.service.deleteLeaves(leave.id).subscribe(
      (resp)=>{
        console.log(resp);
        this.getLeaveDetails();
      },
      (err)=>console.log(err)
    );
  }

  edit(leaveIn:any){
    console.log("inedit");
    this.leaveToUpdate=leaveIn;
    console.log(this.leaveToUpdate);
    this.showModal = true;
    // ($('#exampleModal') as any).modal('show');
  }

  update(){
    this.service.updateLeave(this.leaveToUpdate).subscribe(
      (resp)=>{
        console.log(resp);
      },
      (err)=>console.log(err)
    );
  }

  submitForm() {
    const leaveTypeData = this.registerForm.value;
    console.log(leaveTypeData);

    // Check if the leave type already exists
    const leaveTypeExists = this.leavesedit.find(leave => leave.name === leaveTypeData.name);
    console.log(leaveTypeExists);
    if (leaveTypeExists!=undefined) {
      // Update existing leave type
      console.log(leaveTypeExists.maxLeaves);
      leaveTypeExists.maxLeaves = leaveTypeData.maxLeaves;

      // Call the service method to update the leave type
      this.service.updateLeaveType(leaveTypeExists).subscribe(
        (response) => {

          console.log("Leave type updated:", response);

        },
        (error) => {
          console.error("Error updating leave type:", error);
        }
      );
    } else {
      // Create new leave type
      this.service.createLeaves(leaveTypeData).subscribe(
        (response) => {

          console.log("New leave type created:", response);

        },
        (error) => {
          console.error("Error creating new leave type:", error);
        }
      );
    }
    this.getLeaveDetails();
  }
}

<div class="container">
  <h2>Edit</h2>
  <form [formGroup]="registerForm" (ngSubmit)=submitForm()>
      <div class="form-group">
          <label for="name">Leave Type Name</label>
          <input type="text" class="form-control" id="name" formControlName="name" />
      </div>

      <div class="form-group">
          <label for="maxLeaves">Maximum Leaves</label>
          <input type="number" class="form-control" id="maxLeaves" formControlName="maxLeaves" />
      </div>

      <button type="submit" class="btn btn-outline-secondary mt-3" [disabled]="registerForm.invalid">Submit</button>
  </form>
</div>




<div class="mt-5">
  <table class="table table-bordered">
    <thead class="thead">
      <tr>
        <!-- <th>ID</th> -->
        <th>Leave Type</th>
        <th>Maximum Leaves</th>
        <th>Edit</th>
        <th>Delete</th>
      </tr>
    </thead>
    <tbody class="tbody">
        <tr *ngFor="let leave of leaves">
          <!-- <td> {{leave.id}}</td> -->
          <td>{{leave.name}}</td>
          <td>{{leave.maxLeaves}}</td>
          <td><button type="button" class="btn btn-dark" data-bs-toggle="modal" data-bs-target="#exampleModal" (click)="edit(leave)">Edit</button></td>
          <td><button type="button" class="btn btn-dark" (click)="delete(leave)">Delete</button></td>
        </tr>
    </tbody>
  </table>
</div>

<div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel"
  aria-hidden="true" *ngIf="showModal">{{showModal}}
  <div class="modal-dialog" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLabel">Update or Edit</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
        <form #updateForm="ngForm" (ngSubmit)=update()>
          <div class="form-group">
            <label for="id">Id :</label>
            <input type="number" disabled #id [(ngModel)]="leaveToUpdate.id" name="id" id="id"
              class="form-control" placeholder="Leave Type ID">
          </div>
          <div class="form-group">
              <label for="name">Leave Type Name</label>
              <input type="text" #name [(ngModel)]="leaveToUpdate.name" name="name" class="form-control" id="name"  />
          </div>

          <div class="form-group">
              <label for="maxLeaves">Maximum Leaves</label>
              <input type="number" #maxLeaves [(ngModel)]="leaveToUpdate.maxLeaves" name="maxLeaves" class="form-control" id="maxLeaves" />
          </div>

          <button type="submit" class="btn btn-outline-secondary mt-3" [disabled]="registerForm.invalid">Update</button>
      </form>
      </div>
    </div>
  </div>
</div>
