import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { JwtService } from '../jwt.service';

@Component({
  selector: 'app-edit-leave-type',
  templateUrl: './edit-leave-type.component.html',
  styleUrls: ['./edit-leave-type.component.scss']
})
export class EditLeaveTypeComponent implements OnInit{
  registerForm!: FormGroup ;

  constructor(
    private service:JwtService,
    private fb: FormBuilder
  ){}
  ngOnInit(): void {
    this.registerForm=this.fb.group({
      name:['',[Validators.required]],
      maxLeaves:['',Validators.required]
    })
  }

  submitForm(){
    console.log(this.registerForm.value);
    this.service.createLeaves(this.registerForm.value).subscribe(
      (response)=>{
      console.log(response)
      }
    )
  }

}
