import { Component, OnInit } from '@angular/core';
import { JwtService } from '../jwt.service';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-emp-register',
  templateUrl: './emp-register.component.html',
  styleUrls: ['./emp-register.component.scss']
})
export class EmpRegisterComponent implements OnInit{

  registerForm!: FormGroup ;

  constructor(
    private service:JwtService,
    private fb: FormBuilder
  ){}


  ngOnInit(): void {
    this.registerForm=this.fb.group({
      name:['',[Validators.required]],
      email:['',[Validators.required,Validators.email]],
      password:['',[Validators.required]],
      mobile:['',[Validators.required]],
      personalemail:['',[Validators.required,Validators.email]],
      dateOfJoining:['',[Validators.required]]
    })
  }

  submitForm(){
    console.log(this.registerForm?.value);
    this.service.register(this.registerForm.value).subscribe(
      (response)=>{
      console.log(response)
      }
    )
  }

}
<div class="container">
  <h2>Register</h2>
  <form [formGroup]="registerForm" (ngSubmit)=submitForm()>
      <div class="form-group">
          <label for="name">Name</label>
          <input type="text" class="form-control" id="name" formControlName="name" />
      </div>

      <div class="form-group">
          <label for="email">Email</label>
          <input type="email" class="form-control" id="email" formControlName="email" />
      </div>

      <div class="form-group">
          <label for="password">Password</label>
          <input type="password" class="form-control" id="password" formControlName="password" />
      </div>
      <div class="form-group">
        <label for="personalemail">Personal Email</label>
        <input type="email" class="form-control" id="personalemail" formControlName="personalemail" />
    </div>
    <div class="form-group">
      <label for="mobile">Mobile</label>
      <input type="text" class="form-control" id="mobile" formControlName="mobile" />
  </div>
      <div class="form-group">
          <label for="dateOfJoining">Date of Joining</label>
          <input type="date" class="form-control" id="dateOfJoining" formControlName="dateOfJoining" />
      </div>

      <button type="submit" class="btn btn-primary" [disabled]="registerForm.invalid">Register</button>
  </form>
</div>
