import { Component } from '@angular/core';
import { JwtService } from '../jwt.service';

@Component({
  selector: 'app-datewiseleaves',
  templateUrl: './datewiseleaves.component.html',
  styleUrls: ['./datewiseleaves.component.scss']
})
export class DatewiseleavesComponent {

  leaveSummary: any;
  fromDate: string='';
  toDate: string='';

  constructor(private service:JwtService) {
    this.fetchLeaveSummary();
   }

  ngOnInit(): void {
    // Fetch leave summary initially

  }

  fetchLeaveSummary() {
    // Fetch leave summary based on selected date range
    this.service.getFilteredSummary(this.fromDate, this.toDate).subscribe(
      (resp) => {
        this.leaveSummary=resp;
        console.log(this.leaveSummary)
      },
      (error) => {
        console.error('Error fetching leave summary:', error);
      }
    );
  }

  filterLeaveSummary() {
    // Call fetchLeaveSummary to apply filtering
    this.fetchLeaveSummary();
  }


}
<div class="container">
  <h2>Leave Summary</h2>
  <div class="form-group">
    <label for="fromDate">From Date:</label>
    <input type="date" class="form-control" id="fromDate" [(ngModel)]="fromDate" (change)="filterLeaveSummary()">
  </div>
  <div class="form-group">
    <label for="toDate">To Date:</label>
    <input type="date" class="form-control" id="toDate" [(ngModel)]="toDate" (change)="filterLeaveSummary()">
  </div>
  <div class="form-group">
    <button type="submit" class="btn btn-dark mt-5">Get</button>
  </div>
  <table class="table table-bordered">
    <!-- Table headers -->
    <tbody>
      <!-- Table rows for leave applications -->
    </tbody>
  </table>
</div>
