import { Component } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { JwtService } from '../jwt.service';

@Component({
  selector: 'app-employee',
  templateUrl: './employee.component.html',
  styleUrls: ['./employee.component.scss']
})
export class EmployeeComponent {
  registerForm!: FormGroup ;
  managers!: any[];
  constructor(
    private service:JwtService,
    private fb: FormBuilder
  ){}


  ngOnInit(): void {
    this.initForm();
    this.loadManagers();
  }

  initForm(): void {
    this.registerForm = this.fb.group({
      name: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(6)]],
      personalEmail: ['', Validators.email],
      mobile: ['', Validators.required],
      dateOfJoining: ['', Validators.required],
      managerId: ['', Validators.required]
    });
  }

  loadManagers(): void {
    this.service.getManagers().subscribe(
      (data: any[]) => {
        this.managers = data;
      },

    );
  }


  submitForm(){
    console.log(this.registerForm.value);
    this.service.employeeregister(this.registerForm.value).subscribe(
      (response)=>{
      console.log(response)
      }
    )
  }
}
<div class="container">
  <h2>Register</h2>
  <form [formGroup]="registerForm" (ngSubmit)=submitForm()>
      <div class="form-group">
          <label for="name">Name</label>
          <input type="text" class="form-control" id="name" formControlName="name" />
      </div>

      <div class="form-group">
          <label for="email">Email</label>
          <input type="email" class="form-control" id="email" formControlName="email" />
      </div>

      <div class="form-group">
          <label for="password">Password</label>
          <input type="password" class="form-control" id="password" formControlName="password" />
      </div>
      <div class="form-group">
        <label for="personalEmail">Personal Email</label>
        <input type="email" class="form-control" id="personalEmail" formControlName="personalEmail" />
    </div>
    <div class="form-group">
      <label for="mobile">Mobile</label>
      <input type="text" class="form-control" id="mobile" formControlName="mobile" />
  </div>
      <div class="form-group">
          <label for="dateOfJoining">Date of Joining</label>
          <input type="date" class="form-control" id="dateOfJoining" formControlName="dateOfJoining" />
      </div>
      <!-- <div class="form-group">
        <label for="managerId">Manager Id</label>
        <input type="number" class="form-control" id="managerId" formControlName="managerId" />
    </div> -->
    <div class="form-group">
      <label for="managerId">Manager</label>
      <select class="form-control" id="managerId" formControlName="managerId">
        <option *ngFor="let manager of managers" [value]="manager">{{ manager.name }}</option>
      </select>
    </div>

      <button type="submit" class="btn btn-primary" [disabled]="registerForm.invalid">Register</button>
  </form>
</div>
